//         step: 1,
//       },
//       accessibility: {
//         description: `Blood pressure index ${gender.value}`,
//       },
//     },
//     chart: { type: "line" },
//     series: { name: gender.value, data: series },
//   };
//   return options;
// };

const comparisonChartOptions = (data) => {
  let options = {
    title: { text: "Blood Pressure index comparison by gender" },
  };
  const femaleSeries = dataFilter("FMLE", data);
  const maleSeries = dataFilter("MLE", data);
  maleSeries.forEach((s) => {
    s[1] = s[1] * -1;
  });

  const yAxisMinValue = getMinYAxisValue(maleSeries);
  const yAxisMaxValue = yAxisMinValue * -1;

  options = {
    ...options,
    chart: {
      type: "bar",
    },
    accessibility: {
      point: {
        valueDescriptionFormat: "{index}. Age {xDescription}, {value}%.",
      },
    },
    xAxis: [
      {
        reversed: false,
        labels: {
          step: 1,
        },
        accessibility: {
          description: "Blood pressure index (female)",
        },
      },
      {
        // mirror axis on right side
        opposite: true,
        reversed: false,
        linkedTo: 0,
        labels: {
          step: 1,
        },
        accessibility: {
          description: "Blood pressure index (male)",
        },
      },
    ],
    yAxis: {
      title: {
        text: null,
      },
      min: yAxisMinValue,
      max: yAxisMaxValue,
      labels: {
        formatter: function () {
          return Math.abs(this.value);
        },
      },
      accessibility: {
        description: "Blood pressure index, comparison by gender in Serbia",
      },
    },
    plotOptions: {
      series: {
        stacking: "normal",
        pointWidth: 5,
      },
    },
    tooltip: {
      formatter: function () {
        return (
          "<b>" +
          this.series.name +
          ", year " +
          this.point.category +
          "</b><br/>" +
          "BP index: " +
          Highcharts.numberFormat(Math.abs(this.point.y), 1)
        );
      },
    },
    series: [
      {
        name: "Male",
        data: maleSeries,
      },
      {
        name: "Female",
        data: femaleSeries,
      },
    ],
  };
  return options;
};

const DataVisulization = ({ param }) => {
  const [data, setData] = useState([]);
  const [chartOptions, setChartOptions] = useState({});
  const [rangeValues, setRangeValues] = useState([]);

  const optionSetter = (button) => {
    if (button.key !== "CMP") {
      const options = singleDataChartOptions(button, data);
      setChartOptions(options);
    } else {
      const options = comparisonChartOptions(data);
      setChartOptions(options);
    }
  };

  useEffect(() => {
    // let fetchedData;
    const getData = async () => {
      const data = await DataProvider.getData(param);
      // fetchedData = data;
      setData(data);
      // set chart options for initial render
      // const options = singleDataChartOptions(buttons[0], data);
      // setChartOptions(options);

      formatRangeValues(data);
      // subscription = InputEmmiter.rangeEmmiter.subscribe((values) => {
      //   let options;
      //   if (values.length > 0) {
      //     const dataRange = data?.filter(
      //       (obj) => obj.TimeDimensionValue > values[0]
      //     );
      //     setData(dataRange);
      //     console.log(values);
      //     options = singleDataChartOptions(buttons[0], dataRange);
      //   } else {
      //     options = singleDataChartOptions(buttons[0], data);
      //   }

      //   setChartOptions(options);
      // });
    };

    getData();
  }, [param]);

  useEffect(() => {
    const subscription = InputEmmiter.rangeEmmiter.subscribe((values) => {
      let options;
      if (values.length > 0) {
        const dataRange = data?.filter(
          (obj) => obj.TimeDimensionValue > values[0]
        );
        options = singleDataChartOptions(buttons[0], dataRange);
      } else {
        options = singleDataChartOptions(buttons[0], data);
      }
      setChartOptions(options);
    });
    return () => subscription.unsubscribe();
  }, [data]);

  return (
    <div>
      <h2>{param.toUpperCase()} Graph</h2>
      {buttons.map((b) => {
        return (
          <button
            onClick={() => {
              optionSetter(b);
            }}
            key={b.key}
          >
            {b.value}
          </button>
        );
      })}
      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
    </div>
  );
};

export default DataVisulization;
