import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";
import { useCallback, useEffect, useState } from "react";
import DataProvider from "../services/DataProvider";
import { filter } from "rxjs";

const buttons = [
  { value: "Female", key: "FMLE" },
  { value: "Male", key: "MLE" },
  { value: "Both Genders", key: "BTSX" },
  { value: "Compare", key: "CMP" },
];

const dataFilter = (key, data) => {
  let series;

  series = data
    ?.filter(({ Dim1 }) => Dim1 === key)
    .map(({ TimeDimensionValue, NumericValue }) => [
      parseInt(TimeDimensionValue),
      NumericValue,
    ])
    .sort((a, b) => {
      return a[0] - b[0];
    });

  return series;
};

const singleDataChartOptions = (gender, data) => {
  let options = {
    title: { text: "Blood Pressure index for Serbia population" },
  };
  const series = dataFilter(gender.key, data);
  options = {
    ...options,
    chart: { type: "line" },
    series: { name: gender.value, data: series },
  };
  return options;
};

const comparisonChartOptions = (data) => {
  let options = {
    title: { text: "Blood Pressure index comparison by gender" },
  };
  const femaleSeries = dataFilter("FMLE", data);
  let maleSeries = dataFilter("MLE", data);
  maleSeries.forEach((s) => {
    s[1] = s[1] * -1;
  });
  options = {
    ...options,
    chart: {
      type: "bar",
    },
    accessibility: {
      point: {
        valueDescriptionFormat: "{index}. Age {xDescription}, {value}%.",
      },
    },
    xAxis: [
      {
        reversed: false,
        labels: {
          step: 0.5,
        },
        accessibility: {
          description: "Blood pressure index (female)",
        },
      },
      {
        // mirror axis on right side
        opposite: true,
        reversed: false,
        linkedTo: 0,
        labels: {
          step: 0.5,
        },
        accessibility: {
          description: "Blood pressure index (male)",
        },
      },
    ],
    yAxis: {
      title: {
        text: null,
      },
      labels: {
        formatter: () => {
          return Math.abs(this.value);
        },
      },
      accessibility: {
        description: "Blood pressure index, comparison by gender in Serbia",
      },
    },
    plotOptions: {
      series: {
        stacking: "normal",
      },
    },
    tooltip: {
      formatter: () => {
        return (
          "<b>" +
          this.series.name +
          ", year " +
          this.point.category +
          "</b><br/>" +
          "BP index: " +
          Highcharts.numberFormat(Math.abs(this.point.y), 1)
        );
      },
    },
    series: [
      {
        name: "Female",
        data: femaleSeries,
      },
      {
        name: "Male",
        data: maleSeries,
      },
    ],
  };
  return options;
};

const SerbiaData = () => {
  const [data, setData] = useState([]);
  const [chartOptions, setChartOptions] = useState({});

  const optionSetter = (button) => {
    if (button.key !== "CMP") {
      const options = singleDataChartOptions(button, data);
      setChartOptions(options);
    } else {
      const options = comparisonChartOptions(data);
      setChartOptions(options);
    }
  };

  useEffect(() => {
    const getData = async () => {
      const data = await DataProvider.getSerbiaData();
      setData(data);
      // set chart options for initial render
      const options = singleDataChartOptions(buttons[0], data);
      setChartOptions(options);
    };
    getData();
  }, []);

  return (
    <div>
      <h2>Serbia Graph here</h2>
      {buttons.map((b) => {
        return (
          <button
            onClick={() => {
              optionSetter(b);
            }}
            key={b.key}
          >
            {b.value}
          </button>
        );
      })}
      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
    </div>
  );
};

export default SerbiaData;

